import { Execute } from 'ox/erc7821'
import { describe, expect, test } from 'vitest'

describe('encodeData', () => {
  test('default', () => {
    expect(
      Execute.encodeData([
        {
          data: '0xdeadbeef',
          to: '0xcafebabecafebabecafebabecafebabecafebabe',
          value: 1n,
        },
        {
          data: '0xcafebabe',
          to: '0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef',
          value: 2n,
        },
      ]),
    ).toMatchInlineSnapshot(
      `""`,
    )
  })

  test('behavior: single call', () => {
    expect(
      Execute.encodeData([
        {
          data: '0xdeadbeef',
          to: '0xcafebabecafebabecafebabecafebabecafebabe',
          value: 1000000000000000000n,
        },
      ]),
    ).toMatchInlineSnapshot(
      `""`,
    )
  })

  test('behavior: no data', () => {
    expect(
      Execute.encodeData([
        {
          to: '0xcafebabecafebabecafebabecafebabecafebabe',
          value: 1n,
        },
        {
          to: '0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef',
          value: 2n,
        },
      ]),
    ).toMatchInlineSnapshot(
      `""`,
    )
  })

  test('behavior: no value', () => {
    expect(
      Execute.encodeData([
        {
          data: '0xdeadbeef',
          to: '0xcafebabecafebabecafebabecafebabecafebabe',
        },
        {
          data: '0xcafebabe',
          to: '0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef',
        },
      ]),
    ).toMatchInlineSnapshot(
      `""`,
    )
  })

  test('behavior: empty calls array', () => {
    expect(Execute.encodeData([])).toMatchInlineSnapshot(
      `""`,
    )
  })

  test('behavior: with opData', () => {
    expect(
      Execute.encodeData(
        [
          {
            data: '0xdeadbeef',
            to: '0xcafebabecafebabecafebabecafebabecafebabe',
            value: 1n,
          },
        ],
        { opData: '0x1234567890abcdef' },
      ),
    ).toMatchInlineSnapshot(
      `""`,
    )
  })

  test('behavior: with empty opData', () => {
    expect(
      Execute.encodeData(
        [
          {
            data: '0xdeadbeef',
            to: '0xcafebabecafebabecafebabecafebabecafebabe',
            value: 1n,
          },
        ],
        { opData: '0x' },
      ),
    ).toMatchInlineSnapshot(
      `""`,
    )
  })

  test('behavior: mixed calls with and without data/value', () => {
    expect(
      Execute.encodeData([
        {
          to: '0xcafebabecafebabecafebabecafebabecafebabe',
          value: 100n,
        },
        {
          data: '0xdeadbeef',
          to: '0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef',
        },
        {
          data: '0xcafebabe',
          to: '0x1234567890123456789012345678901234567890',
          value: 200n,
        },
      ]),
    ).toMatchInlineSnapshot(
      `""`,
    )
  })
})

describe('decodeData', () => {
  test('default', () => {
    const calls = [
      {
        data: '0xdeadbeef',
        to: '0xcafebabecafebabecafebabecafebabecafebabe',
        value: 1n,
      },
      {
        data: '0xcafebabe',
        to: '0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef',
        value: 2n,
      },
    ] as const

    const encoded = Execute.encodeData(calls)
    const result = Execute.decodeData(encoded)

    expect(result).toEqual({ calls })
  })

  test('behavior: single call', () => {
    const calls = [
      {
        data: '0xdeadbeef',
        to: '0xcafebabecafebabecafebabecafebabecafebabe',
        value: 1000000000000000000n,
      },
    ] as const

    const encoded = Execute.encodeData(calls)
    const result = Execute.decodeData(encoded)

    expect(result).toEqual({ calls })
  })

  test('behavior: empty calls', () => {
    const calls = [] as const

    const encoded = Execute.encodeData(calls)
    const result = Execute.decodeData(encoded)

    expect(result).toEqual({ calls })
  })

  test('behavior: with opData', () => {
    const calls = [
      {
        data: '0xdeadbeef',
        to: '0xcafebabecafebabecafebabecafebabecafebabe',
        value: 1n,
      },
    ] as const
    const opData = '0x1234567890abcdef' as const

    const encoded = Execute.encodeData(calls, { opData })
    const result = Execute.decodeData(encoded)

    expect(result).toEqual({ calls, opData })
  })

  test('behavior: with empty opData', () => {
    const calls = [
      {
        data: '0xdeadbeef',
        to: '0xcafebabecafebabecafebabecafebabecafebabe',
        value: 1n,
      },
    ] as const
    const opData = '0x' as const

    const encoded = Execute.encodeData(calls, { opData })
    const result = Execute.decodeData(encoded)

    expect(result).toEqual({
      calls,
      opData: undefined,
    })
  })

  test('behavior: no data or value', () => {
    const calls = [
      {
        to: '0xcafebabecafebabecafebabecafebabecafebabe',
        value: 0n,
        data: '0x',
      },
    ] as const

    const encoded = Execute.encodeData(calls)
    const result = Execute.decodeData(encoded)

    expect(result).toEqual({ calls })
  })

  test('behavior: round trip with mixed calls', () => {
    const calls = [
      {
        to: '0xcafebabecafebabecafebabecafebabecafebabe',
        value: 100n,
        data: '0x',
      },
      {
        data: '0xdeadbeef',
        to: '0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef',
        value: 0n,
      },
      {
        data: '0xcafebabe',
        to: '0x1234567890123456789012345678901234567890',
        value: 200n,
      },
    ] as const

    const encoded = Execute.encodeData(calls)
    const result = Execute.decodeData(encoded)

    expect(result).toEqual({ calls })
  })
})

